example.code:
using Gtk;
using Gtk.Moonlight;

class X {
	static void Main ()
	{
		Application.Init ();
		GtkSilver.Init ();
		Window w = new Window ("Sample XAML Content");
		GtkSilver silver = new GtkSilver (100, 100);
		w.Add (silver);
		w.ShowAll ();

		silver.LoadFile ("test-animation-line.xaml");
		Application.Run ();
	}
}
